export const INPUT_LOGIN = 'INPUT_LOGIN';
export const INPUT_WALLET = 'INPUT_WALLET';
export const REQUEST_API = 'REQUEST_API';
export const CURRENCIES_API = 'CURRENCIES_API';
export const FAILED_REQUEST = 'FAILED_REQUEST';

export const user = (email) => ({
  type: INPUT_LOGIN,
  email,
});

export const wallet = () => ({
  type: INPUT_WALLET,
  currencies: [],
  expenses: [],
});

export const requestApi = (response) => ({
  type: REQUEST_API,
  response,
});

export const currenciesApi = () => ({
  type: CURRENCIES_API,
});

export const failedRequest = (error) => ({
  type: FAILED_REQUEST,
  error,
});

export function fecthApi() {
  return async (dispatch) => {
    dispatch(currenciesApi);
    try {
      const result = await fetch('https://economia.awesomeapi.com.br/json/all');
      const response = await result.json();
      return dispatch(requestApi(response));
    } catch (err) {
      return dispatch(failedRequest(err));
    }
  };
}
