import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';
import { updateExpenses } from '../actions';

class ExpenseAddForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: 0,
      value: 0,
      description: '',
      currency: 'USD',
      method: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates: '',
    };
  }

  createrExpenses() {
    const { stateProps } = this.props;
    const expenses = stateProps;

    if (expenses !== undefined) {
      return expenses
        .map(({ id, value, description, currency, method, tag, exchangeRates }) => {
          const { name, ask } = exchangeRates[currency];
          const total = value * Number(ask);
          return (
            <tr key={ id } id={ id }>
              <td name={ description }>{ description }</td>
              <td name={ tag }>{ tag }</td>
              <td name={ method }>{ method }</td>
              <td name={ Number(ask).toFixed(2) }>
                { `${Number(ask).toFixed(2)}` }
              </td>
              <td name={ name.split('/')[0] }>{ name.split('/')[0] }</td>
              <td name={ value }>{ value }</td>
              <td>{ `${total.toFixed(2)}` }</td>
              <td>Real</td>
              <td>
                <button
                  id={ id }
                  type="button"
                  data-testid="edit-btn"
                  onClick={ this.handleClickEdit.bind(this) }
                >
                  Editar
                </button>
              </td>
              <td>
                <button
                  id={ id }
                  type="button"
                  data-testid="delete-btn"
                  onClick={ this.handleClickDelete.bind(this) }
                >
                  Exluir
                </button>
              </td>
            </tr>
          );
        });
    }
  }

  handleClickDelete({ target: { id } }) {
    const { stateProps, expensesUpdateDispatched } = this.props;
    const ids = Number(id);
    const newExpenses = stateProps.map((e) => e).filter((expense) => expense.id !== ids);
    expensesUpdateDispatched(newExpenses);
  }

  handleClickEdit() {
    this.handleEditExpense();
  }

  handleEditExpense() {
    return (
      <div>
        <form>
          <label htmlFor="value">
            Valor:
            { this.createInputs('value', 'number', 'value-input', value) }
          </label>
          <label htmlFor="description">
            Descrição:
            { this.createInputs('description', 'text', 'description-input', description) }
          </label>
          <label htmlFor="currency">
            Moeda:
            <select
              name="currency"
              id="currency"
              data-testid=" currency-input"
              onChange={ this.handleChange }
            >
              { this.createOptions() }
            </select>
          </label>
          <label htmlFor="method">
            Método de pagamento:
            { this.createSelect(methodList, 'method', 'method-input', this.handleChange) }
          </label>
          <label htmlFor="tag">
            Tag:
            { this.createSelect(tagList, 'tag', 'tag-input', this.handleChange) }
          </label>
          <button type="button">
            Editar despesa
          </button>
        </form>
      </div>
    );
  }

  render() {
    return (
      <div>
        <table>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
          { this.createrExpenses() }
        </table>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  stateProps: state.wallet.expenses,
  wallet: state.wallet,
});

const mapDispatchToProps = (dispatch) => ({
  expensesUpdateDispatched: (expenses) => dispatch(updateExpenses(expenses)),
});

export default connect(mapStateToProps, mapDispatchToProps)(ExpenseAddForm);

ExpenseAddForm.propTypes = {
  stateProps: PropTypes.array,
  expensesDispatched: PropTypes.func,
}.isRequired;
