import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { wallet } from '../actions';

const methodList = ['Dinheiro', 'Cartão de crédito', 'Cartão de débito'];
const tagList = ['Alimentação', 'Lazer', 'Trabalho', 'Transporte', 'Saúde'];

class FormWallet extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: 0,
      value: '',
      description: '',
      currency: 'USD',
      method: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates: '',
    };

    this.createOptions = this.createOptions.bind(this);
    this.createSelect = this.createSelect.bind(this);
    this.handleChange = this.handleChange.bind(this);
  }

  createOptions() {
    const { listCurrenciesState } = this.props;
    return listCurrenciesState.map((element) => (
      <option
        key={ element }
        value={ element }
        data-testid={ element }
      >
        { element }
      </option>
    ));
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  async handleClick() {
    const { currencyValuesState: { data }, expensesDispatched } = this.props;
    await this.setState(() => ({
      id: this.state.id + 1,
      exchangeRates: data,
    }));
    expensesDispatched(this.state);
    this.handleTotalValue();
  }

  createSelect(array, name, data, onChange) {
    return (
      <select
        name={ name }
        id={ name }
        data-testid={ data }
        onChange={ onChange }
      >
        {array.map((element) => (
          <option key={ element }>{ element }</option>
        )) }
      </select>
    );
  }

  createInputs(name, type, data, onChange) {
    return (<input
      name={ name }
      id={ name }
      type={ type }
      data-testid={ data }
      onChange={ onChange }
    />);
  }

  handleTotalValue() {
    const { value, exchangeRates, currency } = this.state;
    exchangeRates.find((element) => element === currency);
    console.log(value);
    console.log(currency);
  }

  render() {
    return (
      <div>
        <form>
          <label htmlFor="value">
            Valor
            { this.createInputs('value', 'text', 'value-input', this.handleChange) }
          </label>
          <label htmlFor="currency">
            Moeda
            <select
              name="currency"
              id="currency"
              data-testid=" currency-input"
              onChange={ this.handleChange }
            >
              { this.createOptions() }
            </select>
          </label>
          <label htmlFor="method">
            Método de pagamento
            { this.createSelect(methodList, 'method', 'method-input', this.handleChange) }
          </label>
          <label htmlFor="tag">
            Tag
            { this.createSelect(tagList, 'tag', 'tag-input', this.handleChange) }
          </label>
          <label htmlFor="description">
            Descrição
            { this.createInputs(
            'description', 'text', 'description-input', this.handleChange,
          ) }
          </label>
          <button type="button" onClick={ this.handleClick.bind(this) }>
            Adicionar despesa
          </button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  listCurrenciesState: state.wallet.currencies,
  currencyValuesState: state.wallet.data,
});

const mapDispatchToProps = (dispatch) => ({
  expensesDispatched: (expenses) => dispatch(wallet(expenses)),
});

export default connect(mapStateToProps, mapDispatchToProps)(FormWallet);

FormWallet.propTypes = {
  listCurrenciesState: PropTypes.func.isRequired,
  expensesDispatched: PropTypes.objectOf.isRequired,
  currencyValuesState: PropTypes.objectOf.isRequired,
};
