import React from 'react';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      isDisabled: true,
    };
  }

  onSubmit() {
    preventDefault(this);
  }

  validateEmail(email) {
    const re = /^([\w.%+-]+)@([\w-]+\.)+([\w]{2})$/i;
    return re.test(email);
  }

  handleChange(event) {
    const email = event.target.value;
    const password = event.target.value;
    if (this.validateEmail(email) && password.length >= 6) {
      this.setState({
        email,
        password,
        isDisabled: false,
      });
    }
  }

  render() {
    const { email, password, isDisabled } = this.state;
    return (
      <form>
        <label htmlFor="email">
          Email
          <input
            type="text"
            name="email"
            placeholder="Email"
            data-testid="email-input"
            onChange={ this.handleChange.bind(this) }
          />
        </label>
        <label htmlFor="password">
          Password
          <input
            type="password"
            name="password"
            placeholder="Password"
            data-testid="password-input"
            onChange={ this.handleChange.bind(this) }
          />
        </label>
        <button
          type="submit"
          disabled={ isDisabled }
          onChange={ this.onSubmit.bind(this) }
        >
          ENTRAR
        </button>
      </form>
    );
  }
}

export default Login;
