import React from 'react';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      isDisabled: false,
    };
  }

  handleChange({ target }) {
    const { name, value, isDisabled } = target;
    this.setState({
      [name]: value,
    });
    if (name === 'email') {
      if (this.validateEmail(name)) {
        this.setState({
          isDisabled: true,
        });
      };
    }
}

validateEmail(email) {
  const re = /^([\w.%+-]+)@([\w-]+\.)+([\w]{3})$/i;
  return re.test(email);
}

render() {
  const { email, password } = this.state;
  return (
    <form>
      <label htmlFor="email">
        Email
          <input
          type="text"
          name="email"
          value={ email }
          placeholder="Email"
          data-testid="email-input"
          onChange={ this.handleChange.bind(this) }
        />
      </label>
      <label htmlFor="password">
        Password
          <input
          type="password"
          name="password"
          value={ password }
          placeholder="Password"
          data-testid="password-input"
          onChange={ this.handleChange.bind(this) }
        />
      </label>
      <button
        type="button"
        disabled
      >
        ENTRAR
        </button>
    </form>
  );
}
}

export default Login;
