import React from 'react';
import { Redirect } from 'react-router';

class Login extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      email: '',
      password: '',
      users: [{ user: 'admin@admin.com', pass: '123456' }],
      isDisabled: true,
    };
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value }, () => this.validateUser());
  }

  validateUser() {
    const { users, email, password } = this.state;
    if (users.some((user) => (user.user === email && user.pass === password))) {
      this.setState({ isDisabled: false });
    }
  }

  render() {
    const { email, password, isDisabled } = this.state;
    if (!isDisabled) return <Redirect to="/carteira" />;
    return (
      <form>
        <label htmlFor="email">
          Email
          <input
            type="text"
            name="email"
            value={ email }
            placeholder="Email"
            data-testid="email-input"
            onChange={ this.handleChange.bind(this) }
          />
        </label>
        <label htmlFor="password">
          Password
          <input
            type="password"
            name="password"
            value={ password }
            placeholder="Password"
            data-testid="password-input"
            onChange={ this.handleChange.bind(this) }
          />
        </label>
        <button
          type="button"
          disabled={ isDisabled }
          onClick={ this.handleClick.bind(this) }
        >
          ENTRAR
        </button>
      </form>
    );
  }
}

export default Login;
