import { REQUEST_API, CURRENCIES_API, FAILED_REQUEST } from '../actions';

const INITIAL_STATE = {
  isFetching: false,
  currencies: [],
  error: '',
};

const currencies = (state = INITIAL_STATE, action) => {
  switch (action.type) {
  case REQUEST_API:
    return {
      ...state,
      currencies: action.currenciesObj,
      isFetching: false,
    };
  case CURRENCIES_API:
    return {
      ...state,
      isFetching: true,
    };
  case FAILED_REQUEST:
    return {
      ...state,
      error: action.error,
    };
  default:
    return state;
  }
};

export default currencies;
