import React from 'react';

export default class ExpenseAddForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: 0,
      value: '',
      description: '',
      currency: 'USD',
      method: 'Dinheiro',
      tag: 'Alimentação',
      exchangeRates: '',
    };
  }

  render() {
    const { id, value, description, currency, method, tag } = this.state;
    return (
      <div>
        <table>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
          <tr>
            <td>{ Descrição }</td>
            <td>{ Tag }</td>
            <td>{ Método }</td>
            <td>{ Valor }</td>
            <td>{ Câmbio }</td>
            <td>{ Valor }</td>
            <td>{ Real }</td>
            <button type='button'>Editar/Excluir</button>
          </tr>
        </table>
      </div>
    );
  }
}
