import React from 'react';
import PropTypes from 'prop-types';
import { connect } from 'react-redux';
import { wallet } from '../actions';

class FormWallet extends React.Component {
  constructor(props) {
    super(props);
    this.createOptions = this.createOptions.bind(this);
  }

  createOptions() {
    const { listCurrenciesState } = this.props;
    return listCurrenciesState.map((element) => (
      <option
        key={ element }
        value={ element }
        data-testid={ element }
      >
        { element }
      </option>
    ));
  }

  handleChange({ target }) {
    const { name, value } = target;
    this.setState({ [name]: value });
  }

  render() {
    return (
      <div>
        <form>
          <label htmlFor="valor">
            Valor
            <input name="valor" id="valor" type="text" data-testid="value-input" />
          </label>
          <label htmlFor="moeda">
            Moeda
            <select name="moeda" id="moeda" data-testid=" currency-input">
              { this.createOptions() }
            </select>
          </label>
          <label htmlFor="metodo">
            Método de pagamento
            <select name="metodo" id="metodo" data-testid="method-input">
              <option>Dinheiro</option>
              <option>Cartão de crédito</option>
              <option>Cartão de débito</option>
            </select>
          </label>
          <label htmlFor="tag">
            Tag
            <select name="tag" id="tag" data-testid="tag-input">
              <option>Alimentação</option>
              <option>Lazer</option>
              <option>Trabalho</option>
              <option>Transporte</option>
              <option>Saúde</option>
            </select>
          </label>
          <label htmlFor="descricao">
            Descrição
            <input
              name="descricao"
              id="descricao"
              type="text"
              data-testid="description-input"
            />
          </label>
          <button type="button">Adicionar despesa</button>
        </form>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  listCurrenciesState: state.wallet.currencies,
});

const mapDispatchToProps = (dispatch) => ({
  expensesDispatched: () => dispatch(wallet),
});

export default connect(mapStateToProps, mapDispatchToProps)(FormWallet);

FormWallet.propTypes = {
  listCurrenciesState: PropTypes.func.isRequired,
  expensesDispatched: PropTypes.objectOf.isRequired,
};
