import React from 'react';
import { connect } from 'react-redux';

class ExpenseAddForm extends React.Component {
  render() {
    const { value, description, currency, method, tag, } = this.props;
    return (
      <div>
        <table>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
          <tr>
            <td>{ description }</td>
            <td>{ tag }</td>
            <td>{ method }</td>
            <td>{ value }</td>
            <td>{ currency }</td>
            <td>{ value }</td>
            <td> Real </td>
            <button type="button">Editar/Excluir</button>
          </tr>
        </table>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  stateProps: state.wallet.exponses,
});

export default connect(mapStateToProps)(ExpenseAddForm);
