import React from 'react';
import { connect } from 'react-redux';
import PropTypes from 'prop-types';

class ExpenseAddForm extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      id: '',
      value: '',
      description: '',
      currency: '',
      method: '',
      tag: '',
      exchangeRates: '',
      name: '',
    };
    const { stateProps } = this.props;
  }

  createrExpenses() {
    const expenses = this.props.stateProps;

    expenses.map(({ id, value, description, currency, method, tag, ask, name }) => {
      return (
        <tr key={ id }>
          <th>{ description }</th>
          <th>{ tag }</th>
          <th>{ method }</th>
          <th>{ currency }</th>
          <th>{ value }</th>
          <th>convertValue</th>
          <th>{ `R$ ${ask}` }</th>
          <th>{ name }</th>
          <button type="button" data-testid="edit-btn">Editar</button>
          <button type="button" data-testid="delete-btn">Exluir</button>
        </tr>
      );
    });
  }

  render() {
    return (
      <div>
        <table>
          <tr>
            <th>Descrição</th>
            <th>Tag</th>
            <th>Método de pagamento</th>
            <th>Valor</th>
            <th>Moeda</th>
            <th>Câmbio utilizado</th>
            <th>Valor convertido</th>
            <th>Moeda de conversão</th>
            <th>Editar/Excluir</th>
          </tr>
          <tr>
            <th>{ description }</th>
            <th>{ tag }</th>
            <th>{ method }</th>
            <th>{ currency }</th>
            <th>{ value }</th>
            <th>convertValue</th>
            <th>{ `R$ ${ask}` }</th>
            <th>{ name }</th>
            <button type="button" data-testid="edit-btn">Editar</button>
            <button type="button" data-testid="delete-btn">Exluir</button>
          </tr>

        </table>
      </div>
    );
  }
}

const mapStateToProps = (state) => ({
  stateProps: state.wallet.expenses,
});

export default connect(mapStateToProps)(ExpenseAddForm);

ExpenseAddForm.propTypes = {
  stateProps: PropTypes.array,
}.isRequired;
